}

.not-chopped-btn {
  background: linear-gradient(135deg, #56ab2f, #a8e6cf);
  color: white;
}

.not-chopped-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(86, 171, 47, 0.3);
}

.chopped-btn {
  background: linear-gradient(135deg, #ff416c, #ff4757);
  color: white;
}

.chopped-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(255, 65, 108, 0.3);
}

.loading {
  text-align: center;
  font-size: 1.5rem;
  color: white;
  font-weight: 600;
}

.results-container {
  background: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
  text-align: center;
  max-height: 80vh;
  overflow-y: auto;
}

.results-container h1 {
  font-size: 2.5rem;
  font-weight: 800;
  margin-bottom: 2rem;
  background: linear-gradient(135deg, #667eea, #764ba2);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.results-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;
  margin-bottom: 2rem;
}

.result-column {
  background: white;
  border-radius: 15px;
  padding: 1.5rem;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

.result-column h2 {
  font-size: 1.3rem;
  font-weight: 700;
  margin-bottom: 1rem;
}

.result-column.chopped h2 {
  color: #ff4757;
}

.result-column.not-chopped h2 {
  color: #56ab2f;
}

.player-list {
  max-height: 300px;
  overflow-y: auto;
}

.player-item {
  padding: 0.5rem;
  margin: 0.5rem 0;
  background: #f8f9fa;
  border-radius: 8px;
  font-weight: 500;
  font-size: 0.9rem;
}

.reset-btn {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 12px;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.reset-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

@media (max-width: 480px) {
  .app-container,
  .results-container {
    margin: 1rem;
    padding: 1.5rem;
  }

  .results-grid {
    grid-template-columns: 1fr;
  }

  .button-container {
    flex-direction: column;
  }

  .app-header h1 {
    font-size: 2rem;
  }

  .player-name {
    font-size: 1.5rem;
  }

  .avatar-placeholder {
    width: 100px;
    height: 100px;
    font-size: 2rem;
  }
}

